mapper.select:
SELECT P.POST_IDX, P.COMPANY_IDX, P.POST_TITLE, C.COMPANY_NAME, (SELECT AVG(WC_POINT+WLB_POINT+CC_POINT+PO_POINT+EM_POINT) FROM REVIEW) TOT_POINT, C.COMPANY_EMAIL, C.COMPANY_TEL, D.DUTY_NAME, CR.CAREER_NAME, ED.EDU_NAME, P.POST_SALARY, S.SKILL_NAME, EM.EMP_NAME, CT.CITY_NAME, POST_CONTENT, POST_HIT, TO_CHAR(P.POST_DDATE, '""YYYY"년 "MM"월 "DD"일"') POST_DDATE, TO_CHAR(P.POST_CDATE, '""YYYY"년 "MM"월 "DD"일"') POST_CDATE, TO_CHAR(P.POST_CDATE, '""YYYY"."MM"."DD""') POST_FDATE FROM POST P LEFT JOIN COMPANYS C ON P.COMPANY_IDX = C.COMPANY_IDX LEFT JOIN REVIEW R ON C.COMPANY_IDX = R.COMPANY_IDX LEFT JOIN CAREER CR ON P.CAREER_ID = CR.CAREER_ID LEFT JOIN DUTY D ON P.DUTY_ID = D.DUTY_ID LEFT JOIN EDU ED ON P.EDU_ID = ED.EDU_ID LEFT JOIN EMP EM ON P.EMP_ID = EM.EMP_ID LEFT JOIN CITY CT ON P.CITY_ID = CT.CITY_ID LEFT JOIN POST_SKILL PS ON P.POST_IDX = PS.POST_IDX LEFT JOIN SKILL S ON PS.SKILL_ID = S.SKILL_ID WHERE 1=1
AND P.city_id = #{city_id}
AND P.duty_id = #{duty_id}
AND P.career_id = #{career_id}
AND P.emp_id = #{emp_id}
AND PS.skill_id = #{skill_id}

SELECT R.RESUME_IDX, U.USER_NAME, TRUNC(MONTHS_BETWEEN(TRUNC(SYSDATE), TO_DATE(U.USER_BIRTHDATE,'YYYYMMDD')) / 12) USER_AGE, U.USER_GENDER, SUBSTR(U.USER_BIRTHDATE,0,4) USER_YEAR, U.USER_TEL, U.USER_EMAIL, RESUME_TITLE, EB_NAME, ED.EDU_NAME, S.SKILL_NAME, C.CITY_NAME, D.DUTY_NAME, E.EMP_NAME, RC.CAREER_CNAME, TO_CHAR(RC.CAREER_SDATE,'YYYY.MM') CAREER_SDATE, TO_CHAR(RC.CAREER_EDATE,'YYYY.MM') CAREER_EDATE, TRUNC(MONTHS_BETWEEN(RC.CAREER_EDATE,RC.CAREER_SDATE) / 12, 0) CAREER_YEAR, TRUNC(MOD(MONTHS_BETWEEN(RC.CAREER_EDATE, RC.CAREER_SDATE),12),0) CAREER_MONTH, RC.CAREER_DESCRIPTION, COVER_LETTER, RESUME_HIT, TO_CHAR(RESUME_CDATE,'""YYYY"-"MM"-"DD""') RESUME_CDATE, TO_CHAR(RESUME_FDATE,'""YYYY"-"MM"-"DD""') RESUME_FDATE FROM RESUME R LEFT JOIN DUTY D ON R.DUTY_ID = D.DUTY_ID LEFT JOIN CITY C ON R.CITY_ID = C.CITY_ID LEFT JOIN EMP E ON R.EMP_ID = E.EMP_ID LEFT JOIN EDU ED ON R.EDU_ID = ED.EDU_ID LEFT JOIN USERS U ON R.USER_IDX = U.USER_IDX LEFT JOIN RESUME_SKILL RS ON R.RESUME_IDX = RS.RESUME_IDX LEFT JOIN SKILL S ON RS.SKILL_ID = S.SKILL_ID LEFT JOIN RESUME_CAREER RC ON R.RESUME_IDX = RC.RESUME_IDX WHERE 1=1
AND R.city_id = #{city_id}
AND R.duty_id = #{duty_id}
AND TRUNC(MONTHS_BETWEEN(RC.CAREER_EDATE,RC.CAREER_SDATE) / 12, 0) >= #{career_id}/10
AND TRUNC(MONTHS_BETWEEN(RC.CAREER_EDATE,RC.CAREER_SDATE) / 12, 0) >= #{career_id}/10
AND R.emp_id = #{emp_id}
AND RS.skill_id = #{skill_id}
